import React from "react";
import "../stylesheets/Menu.css";
import data from "../Data/TemplateData.json";
import { useState } from "react";

const Menu = () => {
  const [searchTerm, setSearchTerm] = useState("");
  return (
    <>
      <div className=" row templateContainer">
        <div className="searchInput_Container">
          <input
            id="searchInput"
            type="text"
            placeholder="Search here..."
            onChange={(event) => {
              setSearchTerm(event.target.value);
            }}
          />
        </div>
        <div className="row template_Container">
          {data
            .filter((val) => {
              if (searchTerm == "") {
                return val;
              } else if (
                val.type.toLowerCase().includes(searchTerm.toLowerCase())
              ) {
                return val;
              }
            })
            .map((val) => {
              return (
                <div className="col-4 template" key={val.id}>
                  <img src={val.image} alt="" />
                  <h3>{val.title}</h3>
                  <p className="price">${val.price}</p>
                </div>
              );
            })}
        </div>
      </div>
    </>
  );
};

export default Menu;
